package Programs;

public class dividedBy7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Print num which devided by 7");
		
		for(int i=7; i<100; i++) {
		
			if(i%7==0) System.out.println(i);
		}
		System.out.println("Program finish..");
	}

}
